def solve_sudoku(board):
    """
    Solves any Sudoku puzzle
    """
    def is_valid(board, row, col, num):
        # Check row
        for i in range(9):
            if board[row][i] == num:
                return False
        # Check column
        for i in range(9):
            if board[i][col] == num:
                return False
        # Check box
        box_row = (row // 3) * 3
        box_col = (col // 3) * 3
        for i in range(3):
            for j in range(3):
                if board[box_row + i][box_col + j] == num:
                    return False
        return True

    def solve_puzzle(board):
        # Find empty cell
        for i in range(9):
            for j in range(9):
                if board[i][j] == 0:
                    # Try all numbers
                    for num in range(1, 10):
                        if is_valid(board, i, j, num):
                            board[i][j] = num
                            # Recursively solve the puzzle
                            if solve_puzzle(board):
                                return True
                            board[i][j] = 0
                    return False
        return True

    # Solve the puzzle
    solve_puzzle(board)
    return board

