def knapsack(weights, values, W):
    n = len(weights)
    # Initialize table
    table = [[0 for _ in range(W+1)] for _ in range(n+1)]
    # Populate table
    for i in range(1, n+1):
        for j in range(1, W+1):
            if weights[i-1] <= j:
                table[i][j] = max(values[i-1] + table[i-1][j-weights[i-1]], table[i-1][j])
            else:
                table[i][j] = table[i-1][j]
    return table[n][W]

