def isSubTree(T, S): 

    if S is None: 
        return True

    if T is None: 
        return False

    if areIdentical(T, S): 
        return True

    return isSubTree(T.left, S) or isSubTree(T.right, S) 


def areIdentical(T, S): 

    if T is None and S is None: 
        return True

