from nltk.tokenize import word_tokenize

def classify_sentence(sentence):
    tokens = word_tokenize(sentence)
    tagged = nltk.pos_tag(tokens)
    if any(tag == 'JJ' or tag == 'JJR' or tag == 'JJS' for word, tag in tagged):
        return 'affirmative'
    elif any(tag == 'RB' or tag == 'RBR' or tag == 'RBS' for word, tag in tagged):
        return 'negative'
    else:
        return 'neutral'

