# Solution
import ast
import builtins

class Environment:
    def __init__(self):
        self.globals = {
            'abs': abs,
           'min': min,
           'max': max,
            'pow': pow,
            'round': round,
            'print': print
        }
        self.locals = {}

    def execute(self, code):
        node = ast.parse(code)
        self.evaluate(node.body)

    def evaluate(self, node):
        if isinstance(node, ast.Expr):
            return self.evaluate(node.value)
        elif isinstance(node, ast.Call):
            if isinstance(node.func, ast.Name):
                if node.func.id in self.globals:
                    return self.globals[node.func.id](*node.args)
                else:
                    raise Exception(f'Unknown function: {node.func.id}')
            else:
                raise Exception(f'Unknown function call: {node.func}')
        elif isinstance(node, ast.BinOp):
            return self.evaluate(node.left) + self.evaluate(node.right)
        elif isinstance(node, ast.Num):
            return node.n
        elif isinstance(node, ast.Name):
            if node.id in self.locals:
                return self.locals[node.id]
            else:
                raise Exception(f'Unknown variable: {node.id}')
        else:
            raise Exception(f'Unknown node type: {type(node)}')

