def pascal_triangle(n): 
    # Create an array to store the pascal triangle 
    triangle = [[1 for x in range(i+1)] for i in range(n)] 

    # Iterate through rows 
    for line in range(2, n): 
        for col in range(line): 
            # calculate the number of previous lines 
            # and column number 
            prev_line = line - 1
            prev_col = col 
            if col == 0 or col == prev_line: 
                triangle[line][col] = 1
            else: 
                triangle[line][col] = triangle[prev_line][prev_col] + triangle[prev_line][prev_col+1] 

    # Print the pascal triangle 
    for line in triangle: 
        print(line) 

