def solve_sudoku(board):
    # Find an empty cell
    for i in range(9):
        for j in range(9):
            if board[i][j] == 0:
                # Try each number from 1 to 9
                for num in range(1, 10):
                    # Check if the number is valid in the current position
                    if is_valid(board, i, j, num):
                        board[i][j] = num
                        # Recursively call the function to solve the rest of the board
                        if solve_sudoku(board):
                            return True
                        # If the solution is not found, reset the cell to 0
                        board[i][j] = 0
                # If no valid number is found, return False
                return False
    # If the board is fully solved, return True
    return True

def is_valid(board, row, col, num):
    # Check the row
    for i in range(9):
        if board[row][i] == num:
            return False
    # Check the column
    for i in range(9):
        if board[i][col] == num:
            return False
    # Check the 3x3 box
    start_row = row - row % 3
    start_col = col - col % 3
    for i in range(3):
        for j in range(3):
            if board[i + start_row][j + start_col] == num:
                return False
    # If no valid number is found, return True
    return True

