def is_valid_email(email):
    # Check if email address has an '@' symbol
    if '@' not in email:
        return False

    # Split the email address into local name and domain
    local_name, domain = email.split('@')

    # Check if the local name contains only valid characters
    if not local_name.isalnum():
        return False

    # Check if the domain contains only valid characters
    if not domain.isalnum():
        return False

    # Check if the domain has a length greater than 2
    if len(domain) < 2:
        return False

    # Check if the local name has a length greater than 2
    if len(local_name) < 2:
        return False

