class MazeSolver:
    def __init__(self, maze):
        self.maze = maze
        self.visited = set()

    def solve(self):
        start = self.find_start()
        path = self.bfs(start)
        return path

    def find_start(self):
        for i in range(len(self.maze)):
            for j in range(len(self.maze[i])):
                if self.maze[i][j] == 'S':
                    return (i, j)

    def bfs(self, start):
        queue = [start]
        while queue:
