def evaluatePostfixExpression(postfix):
    stack = []
    for token in postfix.split():
        if token == "+":
            val1 = stack.pop()
            val2 = stack.pop()
            stack.append(val2 + val1)
        elif token == "*":
            val1 = stack.pop()
            val2 = stack.pop()
            stack.append(val2 * val1)
        else:
