def longest_common_substring(s1, s2):
    n1 = len(s1)
    n2 = len(s2)
    dp = [[0] * (n2 + 1) for _ in range(n1 + 1)]
    longest, x_longest = 0, 0
    for x in range(1, n1 + 1):
        for y in range(1, n2 + 1):
            if s1[x - 1] == s2[y - 1]:
                dp[x][y] = dp[x - 1][y - 1] + 1
                if dp[x][y] > longest:
                    longest = dp[x][y]
                    x_longest = x
            else:
                dp[x][y] = 0
    return s1[x_longest - longest: x_longest]

