import hashlib

class Block:
 def __init__(self, index, timestamp, data, hash):
 self.index = index
 self.timestamp = timestamp
 self.data = data
 self.hash = hash

class Blockchain:
 def __init__(self):
 self.chain = [self.create_genesis_block()]

 def create_genesis_block(self):
 return Block(0, "00:00:00", "Genesis Block", "0")

 def add_block(self, new_block):
 new_block.hash = self.hash_block(new_block)
 self.chain.append(new_block)

 def hash_block(self, block):
 string_object = "Block - Index : {} \n".format(block.index) + "Timestamp : {} \n".format(block.timestamp) + "Data : {} \n".format(block.data) + "Hash : {} \n".format(block.hash)
 block.hash = hashlib.sha256(string_object.encode()).hexdigest()
 return block.hash

