def fuzzy_search(string, query):
    string = string.lower()
    query = query.lower()
    n = len(string)
    m = len(query)
    if m > n:
        return []
    start = 0
    result = []
    while start < n-m+1:
        for i in range(m):
            if string[start + i]!= query[i]:
                break
        else:
            result.append(start)
        start += 1
    return result

