def longest_subarray_with_sum(arr, sum): 
    n = len(arr) 

    # Create a table to store results of subproblems 
    table = [0 for k in range(sum + 1)] 

    # Initialize table[0] as 0 
    table[0] = 0

    # Compute table[i] for i = 1 to sum 
    for i in range(1, sum + 1): 
        table[i] = float('inf') 

    # Compute table[i] for i = 1 to sum 
    for i in range(1, sum + 1): 
        for j in range(i - 1, n): 
            if (arr[j] <= i): 
                table[i] = min(table[i], 1 + table[i - arr[j]]) 

