class VersionControlSystem:
    def __init__(self):
        self.repository = {}

    def create_repository(self, name):
        self.repository[name] = {"files": {}, "commits": []}

    def add_file(self, repository_name, file_name, content):
        if repository_name in self.repository:
            self.repository[repository_name]["files"][file_name] = content
        else:
            print("Repository not found.")

    def commit(self, repository_name, message):
        if repository_name in self.repository:
            commit = {"message": message, "files": self.repository[repository_name]["files"].copy()}
            self.repository[repository_name]["commits"].append(commit)
            self.repository[repository_name]["files"] = {}
        else:
            print("Repository not found.")

    def view_commit_history(self, repository_name):
        if repository_name in self.repository:
            for i, commit in enumerate(self.repository[repository_name]["commits"]):
                print(f"Commit {i+1}: {commit['message']}")
                for file, content in commit["files"].items():
                    print(f"  {file}: {content}")
        else:
            print("Repository not found.")

    def view_file_content(self, repository_name, file_name):
        if repository_name in self.repository:
            if file_name in self.repository[repository_name]["files"]:
                print(self.repository[repository_name]["files"][file_name])
            else:
                print("File not found.")
        else:
            print("Repository not found.")


