class Complex:
    def __init__(self, real, imaginary):
        self.real = real
        self.imaginary = imaginary

    def __str__(self):
        return f"{self.real} + {self.imaginary}i"

    def __add__(self, other):
        if isinstance(other, Complex):
            return Complex(self.real + other.real, self.imaginary + other.imaginary)
        else:
            raise TypeError("Can only add Complex numbers")

    def __sub__(self, other):
        if isinstance(other, Complex):
            return Complex(self.real - other.real, self.imaginary - other.imaginary)
        else:
            raise TypeError("Can only subtract Complex numbers")

    def __mul__(self, other):
        if isinstance(other, Complex):
            return Complex(self.real * other.real - self.imaginary * other.imaginary,
                           self.real * other.imaginary + self.imaginary * other.real)
        else:
            raise TypeError("Can only multiply Complex numbers by a number")

    def __truediv__(self, other):
        if isinstance(other, Complex):
            denominator = other.real**2 + other.imaginary**2
            return Complex((self.real * other.real + self.imaginary * other.imaginary) / denominator,
                           (self.imaginary * other.real - self.real * other.imaginary) / denominator)
        else:
            if other == 0:
                raise ZeroDivisionError("Cannot divide by zero")
            return Complex(self.real / other, self.imaginary / other)

    def __eq__(self, other):
        if isinstance(other, Complex):
            return self.real == other.real and self.imaginary == other.imaginary
        else:
            return False

    def __pow__(self, power):
        if isinstance(power, int):
            return Complex(self.real**power, power * self.imaginary * self.real**(power-1))
        else:
            raise TypeError("Power must be an integer")
