def is_valid(board, row, col, num):
    # check row
    for i in range(9):
        if board[row][i] == num:
            return False
    # check column
    for i in range(9):
        if board[i][col] == num:
            return False
    # check box
    box_x = row - row % 3
    box_y = col - col % 3
    for i in range(3):
        for j in range(3):
            if board[i + box_x][j + box_y] == num:
                return False
    return True

