class Maze:
    def __init__(self, maze_map):
        self.maze_map = maze_map
        self.start_pos = (0, 0)
        self.end_pos = (len(maze_map)-1, len(maze_map[0])-1)
        self.visited = set()
        self.path = []

    def find_path(self):
        self.visited.add(self.start_pos)
        self.dfs(self.start_pos)
        return self.path

    def dfs(self, pos):
        if pos == self.end_pos:
            self.path.append(pos)
            return
        else:
            neighbors = self.get_neighbors(pos)
            for neighbor in neighbors:
                if neighbor not in self.visited:
                    self.visited.add(neighbor)
                    self.path.append(neighbor)
                    self.dfs(neighbor)
                    self.path.pop()
                    self.visited.remove(neighbor)

    def get_neighbors(self, pos):
        neighbors = []
        if pos[0] > 0:
            neighbors.append((pos[0]-1, pos[1]))
        if pos[1] > 0:
            neighbors.append((pos[0], pos[1]-1))
        if pos[0] < len(self.maze_map)-1:
            neighbors.append((pos[0]+1, pos[1]))
        if pos[1] < len(self.maze_map[0])-1:
            neighbors.append((pos[0], pos[1]+1))
        return neighbors

def knapsack(weights, values, capacity):
    n = len(weights)
    table = [[0 for x in range(capacity + 1)] for x in range(n + 1)]
    for i in range(n + 1):
        for j in range(capacity + 1):
            if i == 0 or j == 0:
                table[i][j] = 0
            elif weights[i - 1] <= j:
                table[i][j] = max(values[i - 1] + table[i - 1][j - weights[i - 1]], table[i - 1][j])
            else:
                table[i][j] = table[i - 1][j]
    return table[n][capacity]

