def postfixEval(expression):
    stack = []
    for token in expression.split():
        if token.isdigit():
            stack.append(int(token))
        else:
            op2 = stack.pop()
            op1 = stack.pop()
            result = eval(f"{op1} {token} {op2}")
            stack.append(result)
    return stack[0]

