# A utility function to check if a node in tree1 exists in tree2 
def isIdentical(tree1, tree2): 

    # If both trees are empty, return true 
    if tree1 is None and tree2 is None: 
        return True

    # If one of the tree is not empty and other is empty, 
    # return false 
    if tree1 is not None and tree2 is None: 
        return False

    # If data of both the trees is same, then check for left and right subtrees 
    if tree1.data == tree2.data: 
        return isIdentical(tree1.left, tree2.left) and isIdentical(tree1.right, tree2.right) 

    # If data is not same, return false 
    return False

# A utility function to check if the first tree is subtree of the second tree 
def isSubtree(tree1, tree2): 

    # If tree2 is empty, return true 
    if tree2 is None: 
        return True

    # If tree1 is empty, return false 
    if tree1 is None: 
        return False

    # If the data of both the trees is same, then check for left and right subtrees 
    if tree1.data == tree2.data: 
        return isIdentical(tree1, tree2) 

    # If data is not same, check for left subtree 
    return isSubtree(tree1.left, tree2) or isSubtree(tree1.right, tree2)

