def binary_search(nums, val):
    low = 0
    high = len(nums) - 1

    while low <= high:
        mid = (low + high) // 2
        if nums[mid] == val:
            return mid
        elif nums[mid] > val:
            high = mid - 1
        else:
            low = mid + 1
    return -1

