    # create a 2D array to store the results of subproblems
    dp = [[0] * (len(str_2) + 1) for _ in range(len(str_1) + 1)]

    # populate the first row and column
    for i, ch1 in enumerate(str_1):
        for j, ch2 in enumerate(str_2):
            if ch1 == ch2:
                dp[i + 1][j + 1] = dp[i][j] + 1
            else:
                dp[i + 1][j + 1] = 0

    # traverse the 2D array and find the longest substring
    longest_length = 0
    for i in range(len(dp)):
        for j in range(len(dp[0])):
            if dp[i][j] > longest_length:
                longest_length = dp[i][j]

    # return the longest substring
