    # check for the two diagonals
    if board[0][0] == board[1][1] and board[1][1] == board[2][2] and board[2][2] == self.turn:
        return True
    if board[0][2] == board[1][1] and board[1][1] == board[2][0] and board[2][0] == self.turn:
