def get_operator_value(op):
    if op == '+':
        return lambda x, y: x + y
    elif op == '-':
        return lambda x, y: x - y
    elif op == '*':
        return lambda x, y: x * y
    elif op == '/':
        return lambda x, y: x / y

def evaluate_postfix(expression):
    stack = []
    operators = {'+', '-', '*', '/'}

    for token in expression.split():
        if token.isdigit():
            stack.append(int(token))
        elif token in operators:
            b = stack.pop()
            a = stack.pop()
            result = get_operator_value(token)(a, b)
            stack.append(result)
        else:
            raise ValueError(f"Invalid token '{token}' in the expression")

    return stack[0]

