class IntegerStore:
    def __init__(self):
        """Initialize the store with an empty list."""
        self.values = []

    def add(self, value):
        """Add a value to the store if it's an integer."""
        if isinstance(value, int):
            self.values.append(value)
        else:
            raise ValueError("Only integers are allowed.")

    def remove(self, value):
        """Remove a value from the store if it exists."""
        if value in self.values:
            self.values.remove(value)
        else:
            raise ValueError(f"Value {value} not found in the store.")

    def max_value(self):
        """Return the maximum value in the store."""
        if self.values:
            return max(self.values)
        else:
            return None

    def min_value(self):
        """Return the minimum value in the store."""
        if self.values:
            return min(self.values)
        else:
            return None

    def get_all(self):
        """Return all values in the store."""
        return self.values

    def __str__(self):
        """Provide a string representation of the store."""
        return str(self.values)
