class Node:
    def __init__(self):
        self.children = {}
        self.endOfWord = False

class Trie:
    def __init__(self):
        self.root = Node()

    def insert(self, word):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = Node()
            node = node.children[char]
        node.endOfWord = True

    def search(self, word):
        node = self.root
        for char in word:
            if char not in node.children:
                return False
            node = node.children[char]
        return node.endOfWord

    def startsWith(self, prefix):
        node = self.root
        for char in prefix:
            if char not in node.children:
                return []
            node = node.children[char]
        return self._allWordsFromNode(node, prefix)

    def _allWordsFromNode(self, node, prefix):
        words = []
        if node.endOfWord:
            words.append(prefix)
        for char, nextNode in node.children.items():
            words.extend(self._allWordsFromNode(nextNode, prefix + char))
        return words


