def calculate_max_revenue(weights, values):
    n = len(weights)
    table = [[0 for x in range(n+1)] for x in range(n+1)] 

    for i in range(n-1, -1, -1): 
        for j in range(n, 0, -1): 
            if weights[i] <= j: 
                table[i][j] = max(values[i] + table[i][j-weights[i]], table[i+1][j]) 
            else: 
                table[i][j] = table[i+1][j] 
    return table[0][n]

