def isSubtree(s, t): 
    # An empty tree is subtree of a non-empty tree 
    if s == None: 
        return False

    # If trees are identical then return true 
    if areIdentical(s, t): 
        return True

    # If trees are not identical then check for 
    # left and right subtrees 
    return isSubtree(s.left, t) or isSubtree(s.right, t) 

# Function to check if two trees are identical 
def areIdentical(s, t): 
    # An empty tree is identical to another empty tree 
    if s == None and t == None: 
        return True

    # A non-empty tree is identical to another non-empty tree 
    # if and only if 
    # (1) Their root's data is same 
    # (2) Left subtrees are identical 
    # (3) Right subtrees are identical 
    if s!= None and t!= None: 
        return (s.data == t.data and areIdentical(s.left, t.left) and areIdentical(s.right, t.right)) 

    # If neither s nor t are empty 
    return False

