def lcs(x, y): 
    # find length of sequences
    m = len(x)
    n = len(y)

    # create a matrix of size (m+1) x (n+1) 
    dp = [[0 for i in range(n+1)] for i in range(m+1)] 

    # fill the matrix
    for i in range(m): 
        for j in range(n): 
            if x[i] == y[j]: 
                dp[i+1][j+1] = dp[i][j] + 1
            else: 
                dp[i+1][j+1] = max(dp[i+1][j], dp[i][j+1]) 

    # return the length of longest common subsequence
    return dp[m][n]

