def unique_paths(m, n):
    # Create a 2D array to store the number of unique paths to each cell
    dp = [[0 for x in range(n)] for x in range(m)]

    # Fill the first row and first column of the 2D array
    for i in range(m):
        dp[i][0] = 1
    for j in range(n):
        dp[0][j] = 1

    # Calculate the number of unique paths for each cell
    for i in range(1, m):
        for j in range(1, n):
            dp[i][j] = dp[i-1][j] + dp[i][j-1]

    # Return the number of unique paths to the destination
    return dp[m-1][n-1]

