import numpy as np
import gym

# define the environment
class TicTacToeEnv(gym.Env):
    def __init__(self):
        super(TicTacToeEnv, self).__init__()
        self.board = np.zeros((3, 3))
        self.turn = 0
        self.done = False

    def step(self, action):
        self.board[action[0], action[1]] = self.turn
        self.turn = 1 - self.turn
        self.done = self.check_win()
        if not self.done:
            self.done = self.check_draw()
        reward = 0
        if self.done:
            if self.turn == 0:
                reward = 1
            else:
                reward = -1
        return self.board, reward, self.done, {}

    def reset(self):
        self.board = np.zeros((3, 3))
        self.turn = 0
        self.done = False
        return self.board

    def check_win(self):
        # check rows
        for i in range(3):
            if sum(self.board[i, :]) == 3:
                return True
            elif sum(self.board[i, :]) == -3:
                return True
        # check columns
        for i in range(3):
            if sum(self.board[:, i]) == 3:
                return True
            elif sum(self.board[:, i]) == -3:
                return True
        # check diagonals
        if sum(np.diag(self.board)) == 3:
            return True
        elif sum(np.diag(self.board)) == -3:
            return True
        return False

    def check_draw(self):
        for i in range(3):
            for j in range(3):
                if self.board[i, j] == 0:
                    return False
        return True

