#!/usr/bin/env python

import sys
import subprocess
import argparse
import collections

Vote = collections.namedtuple('Vote', ['commit', 'voter'])

def get_votes():
    votes = []
    for line in subprocess.check_output(['git', 'log']).splitlines():
        line = line.decode('utf-8')
        voter, commit = line.split(maxsplit=1)
        votes.append(Vote(commit, voter))
    return votes

def list_votes(votes):
    for vote in votes:
        print('{} voted for {}'.format(vote.voter, vote.commit))

def tally_votes(votes):
    counts = collections.Counter()
    for vote in votes:
        counts[vote.commit] += 1
    return counts

def elect_commit(counts):
    return counts.most_common(1)[0][0]

def main():
    parser = argparse.ArgumentParser()
    parser.add_argument('subcommand')
    args = parser.parse_args()

    if args.subcommand == 'vote':
        subprocess.check_call(['git', 'log', '--pretty=format:%s %aN'])
    elif args.subcommand == 'list':
        votes = get_votes()
        list_votes(votes)
    elif args.subcommand == 'tally':
        votes = get_votes()
        counts = tally_votes(votes)
        print(counts)
    elif args.subcommand == 'elect':
        votes = get_votes()
        counts = tally_votes(votes)
        elect = elect_commit(counts)
        print(elect)

if __name__ == '__main__':
    sys.exit(main())

