import math

def find_closest_point(input_coordinates, set_coordinates):
    closest_point = None
    min_distance = float('inf')
    for point in set_coordinates:
        distance = math.sqrt(
            (point[0] - input_coordinates[0])**2 + 
            (point[1] - input_coordinates[1])**2
        )
        if distance < min_distance:
            min_distance = distance
            closest_point = point
    return closest_point

