def longest_increasing_subsequence(arr):
    max_len = 1
    cur_len = 1
    for i in range(1, len(arr)):
        if arr[i] > arr[i-1]:
            cur_len += 1
            max_len = max(max_len, cur_len)
        else:
            cur_len = 1
    return max_len

def combinations(range):
    if len(range) == 0:
        return 0
    elif len(range) == 1:
        return 1
    else:
        return range[0] * combinations(range[1:]) + combinations(range[1:])

result = combinations(range)
print(result)

