def xor_strings(xs, ys):
    result = []
    for x, y in zip(xs, ys):
        result.append(chr(ord(x) ^ ord(y)))
    return "".join(result)

password = "TestPassword"
string = "TopSecret"

print(xor_strings(password, string))

