```python
def min_diff_partition(arr):
    total_sum = sum(arr)
    n = len(arr)
    if total_sum % 2!= 0:
        return "Cannot divide into two equal parts"

    target_sum = total_sum // 2
    dp = [[False] * (target_sum + 1) for _ in range(n + 1)]

    for i in range(n + 1):
        dp[i][0] = True

    for i in range(1, n + 1):
        for j in range(1, target_sum + 1):
            if j < arr[i-1]:
                dp[i][j] = dp[i-1][j]
            else:
                dp[i][j] = dp[i-1][j] or dp[i-1][j-arr[i-1]]

    if not dp[n][target_sum]:
        return "No partition found"

    partition = []
    i, j = n, target_sum
    while i > 0 and j > 0:
        if dp[i-1][j]:
            i -= 1
        else:
            partition.append(arr[i-1])
            j -= arr[i-1]
            i -= 1

    return partition, list(set(arr) - set(partition))

