import numpy as np

class TicTacToeEnv:
    def __init__(self):
        self.board = np.zeros((3, 3), dtype=int)
        self.current_player = 1
        self.game_over = False
        self.winner = 0
        self.terminal_state = False

    def reset(self):
        self.board = np.zeros((3, 3), dtype=int)
        self.current_player = 1
        self.game_over = False
        self.winner = 0
        self.terminal_state = False
        return self.board

    def step(self, action):
        row, col = action
        if self.board[row, col]!= 0:
            return self.board, 0, self.game_over, {"winner": self.winner}

        self.board[row, col] = self.current_player
        self.current_player = -self.current_player

        # Check for a winner
        if self.check_winner():
            self.game_over = True
            self.winner = self.current_player
            self.terminal_state = True
            return self.board, -1, self.game_over, {"winner": self.winner}

        # Check for a draw
        if np.count_nonzero(self.board) == 9:
            self.game_over = True
            self.winner = 0
            self.terminal_state = True
            return self.board, 0, self.game_over, {"winner": self.winner}

        return self.board, 0, self.game_over, {}

    def check_winner(self):
        # Check rows, columns, and diagonals
        for i in range(3):
            if np.sum(self.board[i, :]) == 3:
                return True
            if np.sum(self.board[:, i]) == 3:
                return True

        if np.sum(np.diag(self.board)) == 3 or np.sum(np.diag(np.fliplr(self.board))) == 3:
            return True

        return False

